import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '../components/Home.vue'; // Ensure this is correct
import UnitsPage from '../components/Units.vue';
import TasksPage from '../components/Tasks.vue';

const routes = [
  { path: '/', redirect: '/home' }, // Redirect root path to '/home'
  { path: '/home', name: 'Home', component: HomePage },
  { path: '/units', name: 'Units', component: UnitsPage },
  { path: '/tasks', name: 'Tasks', component: TasksPage },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL), 
});

export default router;
